import React, { useEffect, useState } from 'react';
import '../styles/global.css';
import axios from 'axios';
import Showresult from './Showresult';

function Body() {

  const [notes, getNotes] = useState('');

  const [state, setState] = useState({ Nombre_de_la_empresa: "", Direccion: "", NIT: "", Telefono: "" });

  useEffect(() => {
    getFiles()
  }, []);

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.value });
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('here');
    if (state.NIT === "" || state.Direccion === "" || state.Nombre_de_la_empresa === "") {
      alert('Enter email and password');
    }
  }

  const getFiles = () => {
    axios.get('http://localhost:8000/api/files')
      .then((res) => {
        const allNotes = res.data;
        getNotes(allNotes);
      })
      .catch(error => console.error(`error: ${error}`));
  }

  return (
    <>
      <div>
        <Showresult notes={notes} />
        <div>
          <div className="signin-content">
            <div className="login-form">
              <h2 className="form-title">Añadir nueva empresa</h2>
              <form
                method="POST"
                onSubmit={handleSubmit}
                className="register-form"
              >
                <div className="form-group">
                  <label htmlFor="text">
                    <i className="zmdi zmdi-email"></i>
                  </label>
                  <input
                    type="text"
                    name='Nombre_de_la_empresa'
                    onChange={handleChange}
                    placeholder="Nombre_de_la_empresa"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="text">
                    <i className="zmdi zmdi-lock"></i>
                  </label>
                  <input
                    type="text"
                    name='Direccion'
                    onChange={handleChange}
                    placeholder="Direccion"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="text">
                    <i className="zmdi zmdi-lock"></i>
                  </label>
                  <input
                    type="text"
                    name='NIT'
                    onChange={handleChange}
                    placeholder="NIT"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="text">
                    <i className="zmdi zmdi-lock"></i>
                  </label>
                  <input
                    type="text"
                    name='Telefono'
                    onChange={handleChange}
                    placeholder="Telefono"
                  />
                </div>
               </form>
               <div className="form-group ">
               <input className="button_empresas"
                type="submit"
                name="signin"
                id="signin"
                value="Añadir empresas"
                />
            </div>
            </div>
          </div>
        </div>
      </div>
  </>
  )
}

export default Body;
