import React, { useEffect, useState } from 'react';
import '../styles/global.css';
import axios from 'axios';
import Showresult from './Showresult';

function Body() {

  const [state, setState] = useState({ Nombre_de_la_empresa: "", Direccion: "", NIT: "", Telefono: "" });

  const [notes, getNotes] = useState('');

  useEffect(() => {
    getFiles()
  }, []);

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.value });
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (state.email === "" || state.password === "") {
      alert('Enter email and password');
    } else {
      const str = `${state.email}:${state.password}`
      const encodeData = btoa(str);
      try {
        await axios.get('http://localhost:5000/connect', { headers: { 'Authorization': `Basic: ${encodeData}` } })
          .then((res) => {
            localStorage.setItem("token", res.data.token);
            axios.get('http://localhost:5000/users/me', { headers: { 'X-Token': localStorage.getItem('token') } })
              .then((res) => {
                const name = res.data.email;
                localStorage.setItem('name', name);
                window.location = "/dash/" + name;
              });
          });
      } catch (e) {
        alert('Email or password invalid!');
      }
    }
  }

  const getFiles = () => {
    axios.get('http://localhost:8000/api/files')
      .then((res) => {
        const allNotes = res.data;
        getNotes(allNotes);
      })
      .catch(error => console.error(`error: ${error}`));
  }

  return (
    <>
      <div>
        <Showresult notes={notes} />
        <div>
          <div className="signin-content">
            <div className="login-form">
              <h2 className="form-title">Añadir nueva empresa</h2>
              <form
                method="GET"
                onSubmit={handleSubmit}
                className="register-form"
              >
                <div className="form-group">
                  <label htmlFor="text">
                    <i className="zmdi zmdi-email"></i>
                  </label>
                  <input
                    type="text"
                    onChange={handleChange}
                    placeholder="Nombre_de_la_empresa"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="text">
                    <i className="zmdi zmdi-lock"></i>
                  </label>
                  <input
                    type="text"
                    onChange={handleChange}
                    placeholder="Direccion"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="text">
                    <i className="zmdi zmdi-lock"></i>
                  </label>
                  <input
                    type="text"
                    onChange={handleChange}
                    placeholder="NIT"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="text">
                    <i className="zmdi zmdi-lock"></i>
                  </label>
                  <input
                    type="text"
                    onChange={handleChange}
                    placeholder="Telefono"
                  />
                </div>
               </form>
               <div className="form-group ">
               <input className="button_empresas"
                type="submit"
                name="signin"
                id="signin"
                value="Añadir empresas"

                />
            </div>
            </div>
          </div>
        </div>
      </div>
      <div className="center_btn">
        <button className="button_empresas">click para añadir empresas</button>
      </div>
  </>
  )
}

export default Body;
