import React, { useEffect, useState } from 'react';
import '../styles/global.css';
import axios from 'axios';
import Showresult from './Showresult';

function Body() {

  const [state, setState] = useState({ Nombre_de_la_empresa: "", Direccion: "", NIT: "", Telefono: "" });

  const [notes, getNotes] = useState('');

  useEffect(() => {
    getFiles()
  }, []);

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.value });
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (state.email === "" || state.password === "") {
      alert('Enter email and password');
    } else {
      const str = `${state.email}:${state.password}`
      const encodeData = btoa(str);
      try {
        await axios.get('http://localhost:5000/connect', { headers: { 'Authorization': `Basic: ${encodeData}`} })
          .then((res) => {
            localStorage.setItem("token", res.data.token);
            axios.get('http://localhost:5000/users/me', { headers: { 'X-Token': localStorage.getItem('token') } })
              .then((res) => {
                const name = res.data.email;
                localStorage.setItem('name', name);
                window.location = "/dash/" + name;
              });
        });
      } catch(e) {
        alert('Email or password invalid!');
      }
    }
  }

  const getFiles = () => {
    axios.get('http://localhost:8000/api/files')
      .then((res) => {
        const allNotes = res.data;
        getNotes(allNotes);
      })
      .catch(error => console.error(`error: ${error}`));
  }

  const addFiles = () => {
    axios.post('')
  }

  return (
    <>
      <div>
        <Showresult notes={notes} />
      <div>
        <form>
          <label for="company_name">Nombre de la empresa</label>
          <input type="text"></input>
          <label for="Direccion">Direccion</label>
          <input></input>
          <input type="text"></input>
          <label for="Direccion">NIT</label>
        </form>
      </div>
      <div className="center_btn">
        <button className="button_empresas">click para a√±adir empresas</button>
      </div>
      </div>
    </>
  )
}

export default Body;
