import React, { useState } from "react";
import { Card, Button } from "react-bootstrap";
import axios from "axios";
import '../styles/global.css';

function Showresult(props) {

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.value });
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (state.email === "" || state.password === "") {
      alert('Enter email and password');
    } else {
      const str = `${state.email}:${state.password}`
      const encodeData = btoa(str);
      try {
        await axios.get('http://localhost:5000/connect', { headers: { 'Authorization': `Basic: ${encodeData}`} })
          .then((res) => {
            localStorage.setItem("token", res.data.token);
            axios.get('http://localhost:5000/users/me', { headers: { 'X-Token': localStorage.getItem('token') } })
              .then((res) => {
                const name = res.data.email;
                localStorage.setItem('name', name);
                window.location = "/dash/" + name;
              });
        });
      } catch(e) {
        alert('Email or password invalid!');
      }
    }
  }

  const displayResult = (props) => {

    const { notes } = props;

    if (notes.length > 0) {
      return (
        notes.map((note, index) => {
          return (
            <Card key={index}>
              <Card.Body>
                <div className="note" key={note.id}>
                  <Card.Title>Nombre de la empresa: {note.Nombre_de_la_empresa}</Card.Title>
                  <p className="note_direction">Direccion: {note.Direccion}</p>
                  <p className="note_nit">NIT: {note.NIT}</p>
                  <p className="note_telefono">Telefono: {note.Telefono}</p>
                </div>
              </Card.Body>
              <Card.Footer>
                <Button className="button_update">Update</Button>
                <Button className="button_delete">Delete</Button>
              </Card.Footer>
            </Card>
          )
        })
      )
    } else {
      return (<h3 className="empresas">No hay empresas.</h3>)
    }

  }

  return (
    <>
      {displayResult(props)}
    </>
  )
}

export default Showresult;
